import * as d3 from "d3";
export default {
  props: {
    nodes: {
      type: Array,
      required: true
    },
    links: {
      type: Array,
      required: true
    },
    parentWidth: Number,
    parentHeight: Number
  },
  mounted() {
    this.drawGraph();
  },
  methods: {
    drawGraph() {
      const svg = d3.select(this.$refs.graph);
      const simulation = d3.forceSimulation().force("link", d3.forceLink().id(function (d) {
        return d.id;
      })).force("charge", d3.forceManyBody().strength(-500)).force("center", d3.forceCenter(svg.attr("width") / 2, svg.attr("height") / 2));
      const nodes = [{
        id: 0,
        name: 'A'
      }, {
        id: 1,
        name: 'B'
      }, {
        id: 2,
        name: 'C'
      }, {
        id: 3,
        name: 'D'
      }, {
        id: 4,
        name: 'E'
      }];
      const links = [{
        source: 0,
        target: 1,
        weight: 5
      }, {
        source: 1,
        target: 2,
        weight: 3
      }, {
        source: 2,
        target: 3,
        weight: 4
      }, {
        source: 3,
        target: 4,
        weight: 6
      }, {
        source: 4,
        target: 0,
        weight: 7
      }, {
        source: 0,
        target: 3,
        weight: 7
      }];
      const link = svg.append("g").selectAll("line").data(this.links).enter().append("line").attr("class", "link").attr("stroke", "#999").style("stroke-width", "2px");
      ;
      const node = svg.append("g").selectAll("circle").data(this.nodes).enter().append("circle").attr("class", "node").attr("r", 20).attr("fill", "lightblue");

      //.style("fill", "#66ccff") // 设置节点的填充颜色
      //.style("stroke", "#fff") // 设置节点的边框颜色
      //.style("stroke-width", "0.1px"); // 设置节点的边框宽度

      function update() {
        node.attr("cx", function (d) {
          return d.x;
        }).attr("cy", function (d) {
          return d.y;
        });
        link.attr("x1", function (d) {
          return d.source.x;
        }).attr("y1", function (d) {
          return d.source.y;
        }).attr("x2", function (d) {
          return d.target.x;
        }).attr("y2", function (d) {
          return d.target.y;
        });
      }
      simulation.nodes(nodes).on("tick", update);
      simulation.force("link").links(links);
      svg.append("defs").append("marker").attr("id", "arrow").attr("viewBox", "0 -5 10 10").attr("refX", 20).attr("refY", 0).attr("markerWidth", 6).attr("markerHeight", 6).attr("orient", "auto").append("path").attr("d", "M0,-5L10,0L0,5").attr("class", "arrow");
      link.attr("marker-end", "url(#arrow)");
    }
  }
};
