import * as d3 from "d3";
export default {
  props: {
    nodes: {
      type: Array,
      required: true
    },
    links: {
      type: Array,
      required: true
    }
  },
  mounted() {
    this.drawGraph();
  },
  methods: {
    drawGraph() {
      const svg = d3.select(this.$refs.graph);
      const simulation = d3.forceSimulation().force("link", d3.forceLink().id(function (d) {
        return d.id;
      })).force("charge", d3.forceManyBody().strength(-200)).force("center", d3.forceCenter(svg.attr("width") / 2, svg.attr("height") / 2));
      const nodes = [{
        id: 0,
        name: 'A'
      }, {
        id: 1,
        name: 'B'
      }, {
        id: 2,
        name: 'C'
      }, {
        id: 3,
        name: 'D'
      }, {
        id: 4,
        name: 'E'
      }];
      const links = [{
        source: 0,
        target: 1,
        weight: 5
      }, {
        source: 1,
        target: 2,
        weight: 3
      }, {
        source: 2,
        target: 3,
        weight: 4
      }, {
        source: 3,
        target: 4,
        weight: 6
      }, {
        source: 4,
        target: 0,
        weight: 7
      }, {
        source: 0,
        target: 3,
        weight: 7
      }, {
        source: 3,
        target: 0,
        weight: 7
      }];
      const link = svg.append("g").selectAll("line").data(links).enter().append("line").attr("class", "link").attr("stroke", "#999").style("stroke-width", "1.5px");
      const node = svg.append("g").selectAll("circle").data(nodes).enter().append("circle").attr("class", "node").attr("r", 10).attr("fill", "lightblue").call(d3.drag().on("start", dragstart).on("drag", dragged).on("end", dragend));

      // 添加文本标签
      const label = svg.append("g").selectAll(null).data(nodes).enter().append("text").attr("text-anchor", "middle").attr("font-size", "12px").attr("dy", "-1.5em").text(d => d.name);
      function update() {
        node.attr("cx", function (d) {
          return d.x;
        }).attr("cy", function (d) {
          return d.y;
        });
        link.attr("x1", function (d) {
          return d.source.x;
        }).attr("y1", function (d) {
          return d.source.y;
        }).attr("x2", function (d) {
          return d.target.x;
        }).attr("y2", function (d) {
          return d.target.y;
        });
        label.attr('x', d => d.x).attr('y', d => d.y);
      }
      simulation.nodes(nodes).on("tick", update);
      simulation.force("link").links(links);
      svg.append("defs").append("marker").attr("id", "arrow").attr("viewBox", "0 -5 10 10").attr("refX", 20).attr("refY", 0).attr("markerWidth", 6).attr("markerHeight", 6).attr("orient", "auto").append("path").attr("d", "M0,-5L10,0L0,5").attr("class", "arrow");
      link.attr("marker-end", "url(#arrow)");
      function dragstart(d) {
        if (!d3.event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
      }
      function dragged(d) {
        d.x = d3.event.x;
        d.y = d3.event.y;
      }
      function dragend(d) {
        if (!d3.event.active) simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
      }
    }
  }
};
