// graphUtils.js

// 引入d3.js库
import * as d3 from 'd3';

// 定义initGraph函数
export function initGraph(container, nodes, links) {
  // 初始化力导向图
  const simulation = d3.forceSimulation(nodes)
    .force('link', d3.forceLink(links).id(d => d.id))
    .force('charge', d3.forceManyBody())
    .force('center', d3.forceCenter(container.Width / 2, container.Height / 2));

  // 创建SVG画布
  const svg = d3.select(container)
    .append('svg')

  // 创建链接
  const link = svg.selectAll('.link')
    .data(links)
    .enter()
    .append('line')
    .attr('class', 'link');

  // 创建节点
  const node = svg.selectAll('.node')
    .data(nodes)
    .enter()
    .append('circle')
    .attr('class', 'node')
    .attr('r', 20)
    .call(d3.drag()
      .on('start', dragstart)
      .on('drag', dragged)
      .on('end', dragend));

    const label = svg
      .selectAll(null)
      .data(nodes)
      .enter()
      .append("text")
      .attr("text-anchor", "middle")
      .attr("font-size", "12px")
      .attr("dy", "-1.5em")
      .text((d) => d.name);
  // 设置节点和链接的属性
  link.attr('stroke', '#ccc')
    .attr('stroke-width', 1);

  node.attr('fill', 'steelblue');


  // 定义节点拖拽的回调函数
  function dragstart(d) {
    if (!d3.event.active) {
      simulation.alphaTarget(0.3).restart();
    }
    d.fx = d.x;
    d.fy = d.y;
  }

  function dragged(d) {
    d.fx = d3.event.x;
    d.fy = d3.event.y;
  }

  function dragend(d) {
    if (!d3.event.active) {
      simulation.alphaTarget(0);
    }
    d.fx = null;
    d.fy = null;
  }

  // 更新节点和链接的位置
  simulation.on('tick', () => {
    link.attr('x1', d => d.source.x)
      .attr('y1', d => d.source.y)
      .attr('x2', d => d.target.x)
      .attr('y2', d => d.target.y);

    node.attr('cx', d => d.x)
      .attr('cy', d => d.y);
  });
}
